import React, { useState } from "react";
import Modal from "./Modal";

const Image = ({ thumbnailUrl, fullSizeUrl }) => {
  const [modalOpen, setModalOpen] = useState(false);

  const openModal = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  return (
    <div>
      <img
        src={thumbnailUrl}
        alt="Thumbnail"
        className="thumbnail-image"
        onClick={openModal}
      />
      {modalOpen && (
        <Modal imageUrl={fullSizeUrl} closeModal={closeModal} />
      )}
    </div>
  );
};

export default Image;
